# [1224] 最大相等频率
#
from sbw import *
# @lc code=start
from sortedcontainers.sorteddict import SortedDict
class Solution:
    def maxEqualFreq(self, nums: List[int]) -> int:
        cnt=defaultdict(int)
        freq=defaultdict(int)
        max_freq=0
        ret=0
        for i,n in enumerate(nums,1):
            if cnt[n]:
                freq[cnt[n]]-=1
            cnt[n]+=1
            freq[cnt[n]]+=1
            max_freq=max(max_freq,cnt[n])
            if max_freq==1 or \
                freq[max_freq]==i or \
                freq[max_freq]==1 and freq[max_freq-1]*(max_freq-1)==i-max_freq or \
                freq[1]==1 and freq[max_freq]*max_freq==i-1:
                ret=i
        return ret

# @lc code=end
