# [1293] 网格中的最短路径
#
from sbw import *
# @lc code=start
import heapq
class Solution:
    def shortestPath(self, grid: List[List[int]], k: int) -> int:

        R,C=len(grid),len(grid[0])
        if k>=R+C-3:
            return R+C-2
        k=min(k,R+C-3)

        q=deque([[0,0,k,0]])
        vis=set()
        vis.add((0,0,k))
        dirs=[-1,0,1,0,-1]
        while q:
            r,c,d,step=q.popleft()
            step+=1
            for i in range(4):
                nr,nc=r+dirs[i],c+dirs[i+1]
                if nr==R-1 and nc==C-1:
                    return step
                if nr<0 or nr==R or nc<0 or nc==C:
                    continue
                if grid[nr][nc]==0:
                    if (nr,nc,d) in vis:
                        continue
                    q.append([nr,nc,d,step])
                    vis.add((nr,nc,d))
                if grid[nr][nc]==1:
                    if d==0 or (nr,nc,d-1) in vis:
                        continue
                    q.append([nr,nc,d-1,step])
                    vis.add((nr,nc,d-1))
        return -1
# @lc code=end
