# @lc app=leetcode.cn id=2556 lang=python3
# @lcpr version=30204
#
# [2556] 二进制矩阵中翻转最多一次使路径不连通
#


# @lcpr-template-start
from sbw import *
# @lcpr-template-end
# @lc code=start
class Solution:
    def isPossibleToCutPath(self, grid: List[List[int]]) -> bool:
        R,C=len(grid),len(grid[0])
        if min(R,C)==1:
            return max(R,C)>2
        
        def dfs(r,c):
            if r==R-1 and c==C-1:
                return True
            grid[r][c]=0
            return (r+1<R and grid[r+1][c] and dfs(r+1,c)) or (c+1<C and grid[r][c+1] and dfs(r,c+1))

        return not dfs(0,0) or not dfs(0,0)
        
# @lc code=end
