#
# [3180] 执行操作可获得的最大总奖励 I
#


# @lcpr-template-start
from sbw import *
# @lcpr-template-end
# @lc code=start
class Solution:
    def maxTotalReward(self, rewardValues: List[int]) -> int:
        s=set(rewardValues)
        mx=max(s)
        if mx-1 in s:
            return mx*2-1
        rewardValues=sorted(s)
        f=1
        for n in rewardValues:
            f|=(f&((1<<n)-1))<<n
        return f.bit_length()-1

# @lc code=end
assert Solution().maxTotalReward([6,13,9,19])==34
